//Merge Sort 
func mergeSort(a []*models.Comparable) {
	l := len(a)
	mSort(a, 0, l-1)
}

func mSort(a []*models.Comparable, lo, hi int) {
	if hi <= lo {
		return
	}
	mid := lo + (hi-lo)/2
	mSort(a, lo, mid)
	mSort(a, mid+1, hi)
	merge(a, lo, mid, hi)
}

//merge 
// lo 
// min 
// hi 
func merge(a []*models.Comparable, lo, mid, hi int) {
	i, j := lo, mid+1
	d := make([]*models.Comparable, 0)
	for _, k := range a {
		d = append(d, k)
	}
	for k := lo; k <= hi; k++ {
		//  
		if i > mid {
			a[k] = d[j]
			j++
			//  
		} else if j > hi {
			a[k] = d[i]
			i++

		} else if less(d[j], d[i]) {
			a[k] = d[j]
			j++
		} else {
			a[k] = d[i]
			i++
		}
	}
}

